    let width: number
    let height: number

    const gradientX1 = 0
    let gradientY1 = 0
    let gradientX2 = 0
    const gradientY2 = 0

    let titleX: number
    let titleY: number
    let titleRotation: number
    let titleVerticalAlign: 'alphabetic' | 'hanging'

    if (direction === 'row') {
        width = length
        height = thickness

        gradientX2 = 1

        let y1: number
        let y2: number

        let textY: number
        const textHorizontalAlign = 'middle'
        let textVerticalAlign: 'alphabetic' | 'hanging'

        titleRotation = 0
        if (titleAlign === 'start') {
            titleX = 0
        } else if (titleAlign === 'middle') {
