        width,
        height,
        gradientX1,
        gradientY1,
        gradientX2,
        gradientY2,
        colorStops,
        ticks: computedTicks,
        titleText: title,
        titleX,
        titleY,
        titleRotation,
        titleHorizontalAlign: titleAlign,
